var IsolationModalDemo = angular.module('FusionStorUI.isolation', []);
IsolationModalDemo.controller('IsolationModalCtrl', ['$scope','$modal','$http', function($scope, $modal, $http) {
    var modalInstance;
    $scope.open = function(url) {  
        $scope.item = {};
        $http.get(url).success(function (data) {
            $scope.templateData = data;
            modalInstance = $modal.open({  
                template : $scope.templateData,
                backdrop:'static',  
                keyboard: false,
                controller : IsolationModalInstanceCtrl,  
                resolve : { 
                    item : function() {  
                        return $scope.item;  
                    }  
                }  
            });  

            modalInstance.opened.then(function() {// 模态窗口打开之后执行的函数  
                
            });  
            modalInstance.result.then(function(result) {  
            }, function(reason) {  
            });  

        });
    };  

    $scope.close = function() {
        modalInstance.dismiss('cancel');  
    }
}]); 
var IsolationModalInstanceCtrl = function($scope, $modalInstance, item) {  
    $scope.item = item;  
    $scope.selected = {  
        item : $scope.item 
    };  
    $scope.ok = function() {  
        submit_access_control_create(false);
    };  
    $scope.update_ok = function() {  
        submit_policy_update(false);
    };  
    $scope.cancel = function() {  
        $modalInstance.dismiss('cancel');  
    };  
};  

