var MailModalDemo = angular.module('FusionStorUI.mail', []);
MailModalDemo.controller('MailModalCtrl', ['$scope','$modal', '$http', function($scope, $modal, $http) {
    var modalInstance;
    $scope.templateData = '';
    $scope.open = function(url) {  
        $scope.item = {};
        $http.get(url).success(function(data){
            $scope.templateData = data;
            modalInstance = $modal.open({  
                template : $scope.templateData,
                controller : MailModalInstanceCtrl,  
                backdrop:'static',  
                keyboard: false,
                resolve : { 
                    item : function() {  
                        return $scope.item;  
                    }  
                }  
            });      
            modalInstance.opened.then(function() {
                
            });  
            modalInstance.result.then(function(result) {  
                
            }, function(reason) {  
                
            });  
        });  
    };  

    $scope.test = function() {  
        submit_mail_test();
    };  

    $scope.save = function() {  
        submit_mail_create();
    };  
    
    $scope.submit_customer = function() {
        submit_support();
    };

    $scope.mail_delete = function() {
        submit_mail_delete();
    };
    
    $scope.support_delete = function() {
        submit_support_delete();
    };

    $scope.close = function() {
        modalInstance.dismiss('cancel');  
    }

}]); 
var MailModalInstanceCtrl = function($scope, $modalInstance, item) {  
    $scope.item = item;  
    $scope.selected = {  
        item : $scope.item 
    };  

    $scope.save = function() {  
    };  

    $scope.update = function() {
        
    };
    $scope.cancel = function() {  
        $modalInstance.dismiss('cancel');  
    };  
}; 
