var FolderModalDemo = angular.module('FusionStorUI.folder', []);
FolderModalDemo.controller('FolderModalCtrl', ['$scope','$modal', '$http', function($scope, $modal, $http) {
    var modalInstance;
    $scope.templateData = '';
    $scope.open = function(url) {  
        $scope.item = {};
        $http.get(url).success(function(data){
            $scope.templateData = data;
            modalInstance = $modal.open({  
                template : $scope.templateData,
                controller : FolderModalInstanceCtrl,  
                backdrop:'static',  
                keyboard: false,
                resolve : { 
                    item : function() {  
                        return $scope.item;  
                    }  
                }  
            });      
        });  
    };  

    $scope.close = function() {
        modalInstance.dismiss('cancel');  
    }
}]); 
var FolderModalInstanceCtrl = function($scope, $modalInstance, item) {  
    $scope.item = item;  
    $scope.selected = {  
        item : $scope.item 
    };  
    $scope.ok = function() {  
        submit_folder_create();
    };  
    $scope.update = function() {
        submit_folder_update();
    };
    $scope.cancel = function() {  
        $modalInstance.dismiss('cancel');  
    };  
};  

