var HostModalDemo = angular.module('FusionStorUI.host', []);
HostModalDemo.controller('HostModalCtrl',['$scope','$modal', function($scope, $modal) {
    var modalInstance;
    $scope.open = function(url) {  
        $scope.item = {};
        modalInstance = $modal.open({  
            backdrop: 'static',       
            keyboard: false,
            templateUrl : url,
            controller : HostModalInstanceCtrl,  
            resolve : { 
                item : function() {  
                    return $scope.item;  
                }  
            }  
        });  
    };  
    $scope.close = function() {
        modalInstance.dismiss('cancel');  
    }
}]); 
var HostModalInstanceCtrl = function($scope, $modalInstance, item) {  
    $scope.item = item;  
    $scope.selected = {  
        item : $scope.item 
    };  
    $scope.ok = function() {  
        submit_host_add();
    };
    
    $scope.cancel = function() {  
        $modalInstance.dismiss('cancel');  
    };  
};  
  
