var RRServerModalDemo = angular.module('FusionStorUI.RRServer', []);
RRServerModalDemo.controller('RRServerModalCtrl', ['$scope','$modal', '$http', function($scope, $modal, $http) {
    var modalInstance;
    $scope.templateData = '';
    $scope.open = function(url) {  
        $scope.item = {};
        $http.get(url).success(function (data) {
            $scope.templateData = data;
            modalInstance = $modal.open({  
                template : $scope.templateData,
                controller : RRServerModalInstanceCtrl,  
                backdrop:'static',  
                keyboard: false,
                resolve : { 
                    item : function() {  
                        return $scope.item;  
                    }  
                }  
            });  
        });
    };  
    $scope.close = function() {
        modalInstance.dismiss('cancel');  
    }
}]); 
var RRServerModalInstanceCtrl = function($scope, $modalInstance, item) {  
    $scope.item = item;  
    $scope.selected = {  
        item : $scope.item 
    };  
    $scope.ok = function() {  
        submit_rrserver_create();
    };  
    $scope.update = function() {
        submit_rrserver_update();
    };
    $scope.cancel = function() {  
        $modalInstance.dismiss('cancel');  
    };  
};  

