var ReportModalDemo = angular.module('FusionStorUI.report', []);
ReportModalDemo.controller('ReportModalCtrl', ['$scope','$modal', '$http', function($scope, $modal, $http) {
    var modalInstance;
    $scope.templateData = '';
    $scope.open = function(url) {  
        $scope.item = {};
        $http.get(url).success(function(data){
            $scope.templateData = data;
            modalInstance = $modal.open({  
                template : $scope.templateData,
                controller : ReportModalInstanceCtrl,  
                backdrop:'static',  
                keyboard: false,
                resolve : { 
                    item : function() {  
                        return $scope.item;  
                    }  
                }  
            });      
            modalInstance.opened.then(function() {
                
            });  
            modalInstance.result.then(function(result) {  
                
            }, function(reason) {  
                
            });  
        });  
    };  
    $scope.close = function() {
        modalInstance.dismiss('cancel');  
    }
}]); 
var ReportModalInstanceCtrl = function($scope, $modalInstance, item) {  
    $scope.item = item;  
    $scope.selected = {  
        item : $scope.item 
    };  
    $scope.ok = function() {  
        submit_report_create();
    };  
    $scope.update = function() {
        
    };
    $scope.cancel = function() {  
        $modalInstance.dismiss('cancel');  
    };  
};  