var SysconfigModalDemo = angular.module('FusionStorUI.sysconfig', []);
SysconfigModalDemo.controller('SysconfigModalCtrl', ['$scope','$modal', '$http', function($scope, $modal, $http) {
    var modalInstance;
    $scope.templateData = '';
    $scope.open = function(url) {  
        $scope.item = {};
        $http.get(url).success(function(data){
            $scope.templateData = data;
            modalInstance = $modal.open({  
                template : $scope.templateData,
                controller : SysconfigModalInstanceCtrl,  
                backdrop:'static',  
                keyboard: false,
                resolve : { 
                    item : function() {  
                        return $scope.item;  
                    }  
                }  
            });  
        });  
    };  
    $scope.close = function() {
        modalInstance.dismiss('cancel');  
    }
}]); 
var SysconfigModalInstanceCtrl = function($scope, $modalInstance, item) {  
    $scope.item = item;  
    $scope.selected = {  
        item : $scope.item 
    };  
    $scope.ok = function() {  
    };  
    $scope.update = function(){  
        submit_sysconfig_update();
    }; 
    $scope.sysconfig_policy_create = function() {
        submit_sysconfig_policy();
    };
    $scope.cancel = function() {  
        $modalInstance.dismiss('cancel');  
    };  
};  
