var enable_submit=true;
function register_teacher(){
var bValid = true;
var c_name=$("#teacher_register_modal").find("login-teacher")
var c_num=$("#teacher_register_modal").find("#teacher_password")
var c_score=$("#teacher_register_modal").find("#teacher_mail")

if(bValid){
if(enable_submit) {
enable_submit = false;
var $submitBtn = $('.modal-footer').find('.btn-blue');
$submitBtn.html('<i class="fa fa-spinner fa-spin" style="margin:3px 6px;"></i>');
 $('.rtn-tip').slideUp('fast');
  $.post("/teacher/create",{
      "teacher_name":$.trim(c_name.val()),
      "password":$.trim(c_score.val()),
      "e_mail":$.trim(c_num.val()),
  },function(data){
      var data=eval("("+data+")");;
      console.log(typeof(data));
      console.log(data);
         if (data.reply.is_success){

           enable_submit = true;
       }else{
       console.log(typeof(data));
        console.log(data);
         var error = data.reply.error;
         $('.rtn-tip').text(error).slideDown('fast');
         $submitBtn.html('ok');
         enable_submit = true;
       }

     });
     }
  }
}

